/**
 * ESLint rule: validate-plugin-manifest-exports
 *
 * Enforce that all plugin UI exports in plugin-manifest.json files use fully-qualified names.
 * Runtime exports can use "register" as a special case for handler registration.
 * This ensures consistency across web and desktop implementations.
 *
 * Example:
 * ✅ CORRECT UI: "export": "RenderX.Plugins.Canvas.CanvasControl"
 * ✅ CORRECT Runtime: "export": "register" (special case for handlers)
 * ❌ WRONG UI:   "export": "CanvasControl"
 */

import fs from "fs";
import path from "path";

export default {
  rules: {
    "validate-plugin-manifest-exports": {
      meta: {
        type: "problem",
        docs: {
          description:
            "Enforce that all plugin exports in plugin-manifest.json use fully-qualified names (namespace.ClassName)",
          category: "Possible Errors",
          recommended: true,
        },
        schema: [],
        messages: {
          notFullyQualified:
            "Plugin '{{pluginId}}' export '{{exportName}}' is not fully-qualified. Use format: Namespace.ClassName (e.g., RenderX.Plugins.Canvas.CanvasControl)",
          invalidFormat:
            "Plugin '{{pluginId}}' export '{{exportName}}' has invalid format. Must contain at least one dot (.) for namespace qualification",
        },
      },

      create(context) {
        // Only run once per lint execution
        if (global.__validatePluginManifestExportsRun) return {};
        global.__validatePluginManifestExportsRun = true;

        const cwd = context.getCwd?.() || process.cwd();
        const reports = [];

        // Find all plugin-manifest.json files
        // Desktop manifests (Avalonia) require fully-qualified exports
        const desktopManifestPaths = [
          path.join(cwd, "src/RenderX.Shell.Avalonia/plugins/plugin-manifest.json"),
        ];

        // Web manifests use simple names for React components
        // Keeping this for documentation purposes, but not used in validation
        // const webManifestPaths = [
        //   path.join(cwd, "catalog/json-plugins/plugin-manifest.json"),
        //   path.join(cwd, "public/plugins/plugin-manifest.json"),
        //   path.join(cwd, "json-plugins/plugin-manifest.json"),
        // ];

        // Validate desktop manifests (require fully-qualified exports)
        for (const manifestPath of desktopManifestPaths) {
          if (!fs.existsSync(manifestPath)) continue;

          try {
            const content = fs.readFileSync(manifestPath, "utf8");
            const manifest = JSON.parse(content);

            if (!manifest.plugins || !Array.isArray(manifest.plugins)) continue;

            for (const plugin of manifest.plugins) {
              // Check UI export - must be fully-qualified for desktop
              if (plugin.ui?.export) {
                const uiExport = plugin.ui.export;
                if (!isFullyQualified(uiExport)) {
                  reports.push({
                    message: `Plugin '${plugin.id}' UI export '${uiExport}' is not fully-qualified. Use format: Namespace.ClassName (e.g., RenderX.Plugins.Canvas.CanvasControl)`,
                    line: 1,
                  });
                }
              }

              // Check runtime export - must be fully-qualified for desktop
              if (plugin.runtime?.export) {
                const runtimeExport = plugin.runtime.export;
                if (!isFullyQualified(runtimeExport)) {
                  reports.push({
                    message: `Plugin '${plugin.id}' runtime export '${runtimeExport}' is not fully-qualified. Use format: Namespace.ClassName (e.g., RenderX.Plugins.Canvas.CanvasPlugin)`,
                    line: 1,
                  });
                }
              }
            }
          } catch (_error) {
            // Silently skip invalid manifests
          }
        }

        // Web manifests are allowed to use simple names for React components
        // No validation needed for web manifests

        // Report all violations at once
        if (reports.length > 0) {
          context.report({
            node: context.sourceCode?.ast || { loc: { start: { line: 1, column: 0 } } },
            message: `Plugin manifest validation failed:\n${reports.map((r) => `  - ${r.message}`).join("\n")}`,
          });
        }

        return {};
      },
    },
  },
};

/**
 * Check if an export name is fully-qualified (contains at least one dot)
 * Examples:
 * - "RenderX.Plugins.Canvas.CanvasControl" → true
 * - "CanvasControl" → false
 * - "Canvas.CanvasControl" → true
 */
function isFullyQualified(exportName) {
  if (!exportName || typeof exportName !== "string") return false;
  return exportName.includes(".");
}

import { readFileSync, writeFileSync } from 'fs';
import { join } from 'path';
import { renderScene } from './render-svg';
import type { Scene } from './scene';

/**
 * Canvas to Graph Converter
 * 
 * Converts the EventRouter canvas file into proper graph configurations
 * that generate SVGs matching the original canvas content.
 * 
 * This establishes the graph configurations as the single source of truth
 * while preserving all the rich animated content from the canvas file.
 */

interface CanvasComponent {
  id: string;
  type: string;
  layout: {
    x: number;
    y: number;
    width: number;
    height: number;
  };
  content: {
    content: string;
    text: string;
    viewBox: string;
    preserveAspectRatio: string;
    svgMarkup: string;
  };
}

interface CanvasFile {
  components: CanvasComponent[];
}

const CANVAS_FILE = join(__dirname, '..', 'assets', 'event-router', 'event_router_storybook.ui');
const SAMPLES_DIR = join(__dirname, '..', 'samples');

function loadCanvasFile(): CanvasFile {
  const content = readFileSync(CANVAS_FILE, 'utf-8');
  return JSON.parse(content) as CanvasFile;
}

function extractSceneTitle(svgMarkup: string): string {
  // Extract title from SVG comment or text elements
  const commentMatch = svgMarkup.match(/<!--\s*Scene\s*\d+:\s*([^-]+)\s*-->/i);
  if (commentMatch) {
    return commentMatch[1].trim();
  }
  
  // Fallback: look for sky text headline
  const headlineMatch = svgMarkup.match(/<text[^>]*font-size="40"[^>]*>([^<]+)</i);
  if (headlineMatch) {
    return headlineMatch[1].replace(/<[^>]*>/g, '').trim();
  }
  
  return 'EventRouter Scene';
}

function cleanSvgMarkup(svgMarkup: string): string[] {
  // Remove the outer <svg> wrapper and extract inner content
  const svgMatch = svgMarkup.match(/<svg[^>]*>([\s\S]*)<\/svg>/);
  const innerContent = svgMatch ? svgMatch[1] : svgMarkup;
  
  // Split into lines and clean up
  return innerContent
    .split('\n')
    .map(line => line.trim())
    .filter(line => line.length > 0);
}

function createSceneConfig(component: CanvasComponent, sceneNumber: number): Scene {
  const title = extractSceneTitle(component.content.svgMarkup);
  const rawSvgLines = cleanSvgMarkup(component.content.svgMarkup);
  
  return {
    id: `event-router-scene-${sceneNumber}-${component.id}`,
    canvas: { 
      width: component.layout.width, 
      height: component.layout.height 
    },
    bg: "#87CEEB", // Sky blue background
    defs: {
      filters: [
        "<filter id=\"sceneShadow\" x=\"-20%\" y=\"-20%\" width=\"140%\" height=\"140%\"><feDropShadow dx=\"0\" dy=\"4\" stdDeviation=\"8\" flood-color=\"#000\" flood-opacity=\"0.25\"/></filter>",
        "<filter id=\"busGlow\"><feGaussianBlur stdDeviation=\"2\" result=\"b\"/><feMerge><feMergeNode in=\"b\"/><feMergeNode in=\"SourceGraphic\"/></feMerge></filter>"
      ],
      gradients: [
        "<linearGradient id=\"roadGradient\" x1=\"0\" y1=\"0\" x2=\"1\" y2=\"0\"><stop offset=\"0\" stop-color=\"#404040\"/><stop offset=\"0.5\" stop-color=\"#505050\"/><stop offset=\"1\" stop-color=\"#404040\"/></linearGradient>",
        "<linearGradient id=\"busGradient\" x1=\"0\" y1=\"0\" x2=\"1\" y2=\"0\"><stop offset=\"0\" stop-color=\"#FFD700\"/><stop offset=\"0.5\" stop-color=\"#FFA500\"/><stop offset=\"1\" stop-color=\"#FFD700\"/></linearGradient>"
      ],
      rawSvg: rawSvgLines
    },
    nodes: [
      {
        kind: "raw-svg",
        id: `scene-${sceneNumber}-content`,
        at: { x: 0, y: 0 },
        size: {
          width: component.layout.width,
          height: component.layout.height
        },
        rawSvg: "USE_DEFS_RAWSVG"
      }
    ]
  };
}

function saveFile(filename: string, content: string): void {
  const filePath = join(SAMPLES_DIR, filename);
  writeFileSync(filePath, content, 'utf-8');
  console.log(`✅ Saved: ${filename}`);
}

async function main(): Promise<void> {
  console.log('🔄 Canvas to Graph Converter');
  console.log('Converting EventRouter canvas file to graph configurations...\n');
  
  try {
    // Load canvas file
    const canvas = loadCanvasFile();
    console.log(`📂 Loaded canvas file with ${canvas.components.length} components\n`);
    
    const sceneResults: Array<{
      sceneNumber: number;
      title: string;
      configFile: string;
      svgFile: string;
      success: boolean;
    }> = [];
    
    // Process each scene
    canvas.components.forEach((component, index) => {
      if (component.type === 'svg' && component.content.svgMarkup) {
        const sceneNumber = index + 1;
        const title = extractSceneTitle(component.content.svgMarkup);
        
        console.log(`📄 Processing Scene ${sceneNumber}: ${title}`);
        console.log(`   Component ID: ${component.id}`);
        console.log(`   Layout: ${component.layout.width}x${component.layout.height} at (${component.layout.x}, ${component.layout.y})`);
        
        try {
          // Create scene configuration
          const sceneConfig = createSceneConfig(component, sceneNumber);
          const configFilename = `graph-scene-${sceneNumber}-${component.id}.json`;
          saveFile(configFilename, JSON.stringify(sceneConfig, null, 2));
          
          // Generate SVG from graph configuration
          const svg = renderScene(sceneConfig);
          const svgFilename = `graph-scene-${sceneNumber}-${component.id}.svg`;
          saveFile(svgFilename, svg);
          
          sceneResults.push({
            sceneNumber,
            title,
            configFile: configFilename,
            svgFile: svgFilename,
            success: true
          });
          
          console.log(`   ✅ Successfully converted to graph configuration`);
          console.log(`   📊 Raw SVG lines: ${sceneConfig.defs?.rawSvg?.length || 0}\n`);
          
        } catch (error) {
          console.error(`   ❌ Error processing scene ${sceneNumber}:`, error);
          sceneResults.push({
            sceneNumber,
            title,
            configFile: '',
            svgFile: '',
            success: false
          });
        }
      }
    });
    
    // Generate summary report
    console.log('='.repeat(80));
    console.log('📋 CANVAS TO GRAPH CONVERSION SUMMARY');
    console.log('='.repeat(80));
    
    const successful = sceneResults.filter(r => r.success);
    const failed = sceneResults.filter(r => !r.success);
    
    console.log(`\n🎯 **Conversion Results:**`);
    console.log(`   • Total scenes processed: ${sceneResults.length}`);
    console.log(`   • Successfully converted: ${successful.length}`);
    console.log(`   • Failed conversions: ${failed.length}`);
    
    if (successful.length > 0) {
      console.log(`\n📁 **Successfully Converted Scenes:**`);
      successful.forEach((result, index) => {
        console.log(`   ${index + 1}. Scene ${result.sceneNumber}: ${result.title}`);
        console.log(`      Config: ${result.configFile}`);
        console.log(`      SVG: ${result.svgFile}`);
      });
    }
    
    console.log(`\n🎊 **Canvas to Graph Conversion Complete!**`);
    console.log(`\nAll EventRouter scenes have been converted from canvas format to`);
    console.log(`graph configurations that serve as the single source of truth.`);
    console.log(`\nThe graph configurations now generate SVGs that match the original`);
    console.log(`canvas content, including all animations and visual details.`);
    
  } catch (error) {
    console.error('❌ Error during conversion:', error);
    process.exit(1);
  }
}

// Run the converter
if (require.main === module) {
  main().catch(console.error);
}

export { main as runCanvasToGraphConverter };

// cinematic-renderer.ts
import type { MonoGraph, MonoScene } from './mono-graph-types';

/**
 * Cinematic Renderer for Phase 2
 * 
 * Creates movie-like presentations with scene transitions, timing controls,
 * and full-screen scene views with smooth transitions between scenes.
 */

export interface CinematicScene extends MonoScene {
  // Enhanced cinematic properties
  cinematic: {
    duration: number; // Scene display duration in seconds
    transition: {
      type: 'fade' | 'slide' | 'zoom' | 'custom';
      duration: number; // Transition duration in seconds
      direction?: 'left' | 'right' | 'up' | 'down';
      easing?: string; // CSS easing function
    };
    viewport: {
      width: number;
      height: number;
      scale?: number; // Zoom level for this scene
    };
  };
}

export interface CinematicPresentation {
  id: string;
  title: string;
  canvas: {
    width: number;
    height: number;
  };
  scenes: CinematicScene[];
  globalAnimations: any[]; // Cross-scene animations (like bus travel)
  controls: {
    autoPlay: boolean;
    loop: boolean;
    showProgress: boolean;
    allowManualNavigation: boolean;
  };
}

/**
 * Convert a mono graph to cinematic presentation
 */
export function createCinematicPresentation(monoGraph: MonoGraph): CinematicPresentation {
  const sceneViewport = {
    width: 1200,
    height: 800
  };
  
  const cinematicScenes: CinematicScene[] = monoGraph.scenes.map((scene, index) => ({
    ...scene,
    cinematic: {
      duration: 5, // 5 seconds per scene
      transition: {
        type: index % 2 === 0 ? 'slide' : 'fade',
        duration: 1, // 1 second transition
        direction: index % 2 === 0 ? 'right' : undefined,
        easing: 'ease-in-out'
      },
      viewport: sceneViewport
    }
  }));
  
  return {
    id: `${monoGraph.id}-cinematic`,
    title: `${monoGraph.title} - Cinematic Presentation`,
    canvas: {
      width: sceneViewport.width,
      height: sceneViewport.height
    },
    scenes: cinematicScenes,
    globalAnimations: [], // Will be populated with bus travel animations
    controls: {
      autoPlay: true,
      loop: true,
      showProgress: true,
      allowManualNavigation: true
    }
  };
}

/**
 * Generate CSS animations for scene transitions
 */
function generateTransitionCSS(scenes: CinematicScene[]): string {
  const transitions = scenes.map((scene, index) => {
    const { transition } = scene.cinematic;
    const nextIndex = (index + 1) % scenes.length;
    
    let transformStart = '';
    let transformEnd = '';
    
    switch (transition.type) {
      case 'slide':
        if (transition.direction === 'right') {
          transformStart = 'translateX(100%)';
          transformEnd = 'translateX(0)';
        } else if (transition.direction === 'left') {
          transformStart = 'translateX(-100%)';
          transformEnd = 'translateX(0)';
        } else if (transition.direction === 'up') {
          transformStart = 'translateY(-100%)';
          transformEnd = 'translateY(0)';
        } else if (transition.direction === 'down') {
          transformStart = 'translateY(100%)';
          transformEnd = 'translateY(0)';
        }
        break;
      case 'zoom':
        transformStart = 'scale(0.5)';
        transformEnd = 'scale(1)';
        break;
      case 'fade':
      default:
        // Fade transitions handled via opacity
        break;
    }
    
    return `
      .scene-${index + 1}-enter {
        animation: scene-${index + 1}-enter-animation ${transition.duration}s ${transition.easing || 'ease-in-out'};
      }
      
      .scene-${index + 1}-exit {
        animation: scene-${index + 1}-exit-animation ${transition.duration}s ${transition.easing || 'ease-in-out'};
      }
      
      @keyframes scene-${index + 1}-enter-animation {
        from {
          opacity: ${transition.type === 'fade' ? '0' : '1'};
          transform: ${transformStart};
        }
        to {
          opacity: 1;
          transform: ${transformEnd};
        }
      }
      
      @keyframes scene-${index + 1}-exit-animation {
        from {
          opacity: 1;
          transform: ${transformEnd};
        }
        to {
          opacity: ${transition.type === 'fade' ? '0' : '1'};
          transform: ${transformStart};
        }
      }
    `;
  }).join('\n');
  
  return `
    <style>
      .cinematic-container {
        width: 100%;
        height: 100%;
        overflow: hidden;
        position: relative;
        background: #000;
      }
      
      .scene-container {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        opacity: 0;
        transform: translateX(100%);
      }
      
      .scene-container.active {
        opacity: 1;
        transform: translateX(0);
      }
      
      .progress-bar {
        position: absolute;
        bottom: 20px;
        left: 50%;
        transform: translateX(-50%);
        width: 80%;
        height: 4px;
        background: rgba(255, 255, 255, 0.3);
        border-radius: 2px;
        overflow: hidden;
      }
      
      .progress-fill {
        height: 100%;
        background: #4CAF50;
        width: 0%;
        transition: width 0.1s ease;
      }
      
      .controls {
        position: absolute;
        bottom: 40px;
        left: 50%;
        transform: translateX(-50%);
        display: flex;
        gap: 10px;
      }
      
      .control-button {
        background: rgba(255, 255, 255, 0.8);
        border: none;
        border-radius: 4px;
        padding: 8px 16px;
        cursor: pointer;
        font-size: 14px;
        transition: background 0.2s ease;
      }
      
      .control-button:hover {
        background: rgba(255, 255, 255, 1);
      }
      
      .scene-title {
        position: absolute;
        top: 20px;
        left: 50%;
        transform: translateX(-50%);
        color: white;
        font-family: Arial, sans-serif;
        font-size: 24px;
        font-weight: bold;
        text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.8);
        z-index: 10;
      }
      
      ${transitions}
    </style>
  `;
}

/**
 * Generate JavaScript for presentation control
 */
function generatePresentationJS(presentation: CinematicPresentation): string {
  const totalDuration = presentation.scenes.reduce((sum, scene) => sum + scene.cinematic.duration + scene.cinematic.transition.duration, 0);
  
  return `
    <script>
      class CinematicPresentation {
        constructor() {
          this.currentScene = 0;
          this.totalScenes = ${presentation.scenes.length};
          this.isPlaying = ${presentation.controls.autoPlay};
          this.totalDuration = ${totalDuration};
          this.startTime = Date.now();
          
          this.initializeControls();
          this.startPresentation();
        }
        
        initializeControls() {
          const playPauseBtn = document.getElementById('play-pause-btn');
          const prevBtn = document.getElementById('prev-btn');
          const nextBtn = document.getElementById('next-btn');
          const restartBtn = document.getElementById('restart-btn');
          
          if (playPauseBtn) {
            playPauseBtn.addEventListener('click', () => this.togglePlayPause());
          }
          
          if (prevBtn) {
            prevBtn.addEventListener('click', () => this.previousScene());
          }
          
          if (nextBtn) {
            nextBtn.addEventListener('click', () => this.nextScene());
          }
          
          if (restartBtn) {
            restartBtn.addEventListener('click', () => this.restart());
          }
          
          // Keyboard controls
          document.addEventListener('keydown', (e) => {
            switch(e.key) {
              case ' ':
                e.preventDefault();
                this.togglePlayPause();
                break;
              case 'ArrowLeft':
                this.previousScene();
                break;
              case 'ArrowRight':
                this.nextScene();
                break;
              case 'r':
                this.restart();
                break;
            }
          });
        }
        
        startPresentation() {
          if (this.isPlaying) {
            this.showScene(0);
            this.scheduleNextScene();
          }
        }
        
        showScene(sceneIndex) {
          const scenes = document.querySelectorAll('.scene-container');
          const titles = document.querySelectorAll('.scene-title');
          
          // Hide all scenes
          scenes.forEach((scene, index) => {
            scene.classList.remove('active');
            if (index === sceneIndex) {
              setTimeout(() => {
                scene.classList.add('active');
              }, 100);
            }
          });
          
          // Update title
          titles.forEach((title, index) => {
            title.style.display = index === sceneIndex ? 'block' : 'none';
          });
          
          this.currentScene = sceneIndex;
          this.updateProgress();
        }
        
        scheduleNextScene() {
          if (!this.isPlaying) return;
          
          const currentSceneData = ${JSON.stringify(presentation.scenes)};
          const sceneDuration = currentSceneData[this.currentScene].cinematic.duration * 1000;
          
          setTimeout(() => {
            if (this.isPlaying) {
              this.nextScene();
            }
          }, sceneDuration);
        }
        
        nextScene() {
          const nextIndex = (this.currentScene + 1) % this.totalScenes;
          this.showScene(nextIndex);
          
          if (this.isPlaying && (nextIndex !== 0 || ${presentation.controls.loop})) {
            this.scheduleNextScene();
          } else if (nextIndex === 0 && !${presentation.controls.loop}) {
            this.isPlaying = false;
            this.updatePlayPauseButton();
          }
        }
        
        previousScene() {
          const prevIndex = this.currentScene === 0 ? this.totalScenes - 1 : this.currentScene - 1;
          this.showScene(prevIndex);
        }
        
        togglePlayPause() {
          this.isPlaying = !this.isPlaying;
          this.updatePlayPauseButton();
          
          if (this.isPlaying) {
            this.scheduleNextScene();
          }
        }
        
        updatePlayPauseButton() {
          const btn = document.getElementById('play-pause-btn');
          if (btn) {
            btn.textContent = this.isPlaying ? 'Pause' : 'Play';
          }
        }
        
        restart() {
          this.currentScene = 0;
          this.startTime = Date.now();
          this.showScene(0);
          
          if (this.isPlaying) {
            this.scheduleNextScene();
          }
        }
        
        updateProgress() {
          const progressFill = document.querySelector('.progress-fill');
          if (progressFill) {
            const progress = ((this.currentScene + 1) / this.totalScenes) * 100;
            progressFill.style.width = progress + '%';
          }
        }
      }
      
      // Initialize presentation when DOM is loaded
      document.addEventListener('DOMContentLoaded', () => {
        new CinematicPresentation();
      });
    </script>
  `;
}

/**
 * Render cinematic presentation to HTML/SVG
 */
/**
 * Save cinematic presentation to HTML file
 */
export function saveCinematicPresentation(presentation: CinematicPresentation, filename: string): void {
  const { writeFileSync } = require('fs');
  const { join } = require('path');

  const html = renderCinematicPresentation(presentation);
  const samplesDir = join(__dirname, '..', 'samples');
  const filePath = join(samplesDir, filename);

  writeFileSync(filePath, html);
}

/**
 * Render cinematic presentation to HTML/SVG
 */
export function renderCinematicPresentation(presentation: CinematicPresentation): string {
  const css = generateTransitionCSS(presentation.scenes);
  const js = generatePresentationJS(presentation);
  
  const scenesHTML = presentation.scenes.map((scene, index) => {
    // Render each scene as a full-screen SVG
    const sceneContent = scene.defs?.rawSvg?.join('\n') || '';
    
    return `
      <div class="scene-container" id="scene-${index + 1}">
        <div class="scene-title">
          Scene ${index + 1}: ${scene.id.replace('event-router-scene-', '').replace('-', ' ').toUpperCase()}
        </div>
        <svg width="100%" height="100%" viewBox="0 0 ${scene.canvas.width} ${scene.canvas.height}" preserveAspectRatio="xMidYMid meet">
          <defs>
            ${scene.defs?.filters?.join('\n') || ''}
            ${scene.defs?.gradients?.join('\n') || ''}
          </defs>
          <rect width="100%" height="100%" fill="${scene.bg || '#87CEEB'}"/>
          ${sceneContent}
        </svg>
      </div>
    `;
  }).join('\n');
  
  const controlsHTML = presentation.controls.allowManualNavigation ? `
    <div class="controls">
      <button class="control-button" id="restart-btn">Restart</button>
      <button class="control-button" id="prev-btn">Previous</button>
      <button class="control-button" id="play-pause-btn">${presentation.controls.autoPlay ? 'Pause' : 'Play'}</button>
      <button class="control-button" id="next-btn">Next</button>
    </div>
  ` : '';
  
  const progressHTML = presentation.controls.showProgress ? `
    <div class="progress-bar">
      <div class="progress-fill"></div>
    </div>
  ` : '';
  
  return `<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>${presentation.title}</title>
  ${css}
</head>
<body>
  <div class="cinematic-container">
    ${scenesHTML}
    ${progressHTML}
    ${controlsHTML}
  </div>
  ${js}
</body>
</html>`;
}

#!/usr/bin/env node
// cli.ts - Enhanced CLI with configuration-driven generation
import type { Graph } from "./graph";
import { graphToSVG } from "./svg-export";
import { genericSceneGenerator } from "./generic-scene-generator";
import { genericGraphGenerator } from "./generic-graph-generator";
import { configLoader } from "./config-loader";
import * as fs from "fs";

