      console.log(`   Layouts (${configs.layouts.length}): ${configs.layouts.join(", ")}`);
      console.log(`   Templates (${configs.templates.length}): ${configs.templates.join(", ")}`);
      break;
    case "sprites":
      console.log("🎨 Available Sprite Libraries:");
