          { id: "user", label: "User Service" },
          { id: "order", label: "Order Service" },
          { id: "payment", label: "Payment Service" },
          { id: "userdb", label: "User DB" },
          { id: "orderdb", label: "Order DB" },
          { id: "paymentdb", label: "Payment DB" }
        ],
        edges: [
          { from: "gateway", to: "auth" },
