        expect(connection.toScene).toBe(monoGraph.scenes[index + 1].id);
        expect(connection.duration).toBe(1);
        expect(connection.path).toMatch(/^M \d+,\d+ Q \d+,\d+ \d+,\d+$/); // SVG path format
      });
