      expect(svgContent).toMatch(/late.*subscriber/i);
      expect(svgContent).toMatch(/replay.*cache/i);
      expect(svgContent).toMatch(/bus[\s\S]*?animateTransform[\s\S]*?translate/i);
      expect(svgContent).toMatch(/subscriber[\s\S]*?animate/i);
