export default function Counter(props) {
  const [count, setCount] = React.useState(0);

  const handleIncrement = () => {
    const newCount = count + 1;
    setCount(newCount);
    // Publish event back to conductor
    if (window.RenderX && window.RenderX.publish) {
      window.RenderX.publish('react.component.counter.incremented', { count: newCount });
    }
  };

  const handleReset = () => {
    setCount(0);
    // Publish event back to conductor
    if (window.RenderX && window.RenderX.publish) {
      window.RenderX.publish('react.component.counter.reset', { count: 0 });
    }
  };

  return (
    <div style={{ padding: '16px', border: '1px solid #ccc', borderRadius: '4px' }}>
      <h3>React Counter</h3>
      <p>Count: {count}</p>
      <button onClick={handleIncrement} style={{ padding: '8px 16px', marginRight: '8px' }}>
        Increment
      </button>
      <button onClick={handleReset} style={{ padding: '8px 16px' }}>
        Reset
      </button>
      <p style={{ marginTop: '12px', fontSize: '12px', color: '#666' }}>
        Try clicking the buttons to test interactivity!
      </p>
    </div>
  );
}
