export default function ThemeToggle(props) {
  const [isDarkMode, setIsDarkMode] = React.useState(false);

  const handleThemeToggle = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    if (window.RenderX && window.RenderX.publish) {
      window.RenderX.publish('react.component.theme.toggled', { 
        isDarkMode: newMode,
        theme: newMode ? 'dark' : 'light'
      });
    }
  };

  const bgColor = isDarkMode ? '#1e1e1e' : '#ffffff';
  const textColor = isDarkMode ? '#ffffff' : '#000000';
  const borderColor = isDarkMode ? '#444444' : '#cccccc';
  const buttonBg = isDarkMode ? '#333333' : '#f0f0f0';
  const buttonHoverBg = isDarkMode ? '#444444' : '#e0e0e0';
  const borderStyle = '2px solid ' + borderColor;

  return (
    <div style={{
      padding: '20px',
      border: borderStyle,
      borderRadius: '8px',
      backgroundColor: bgColor,
      color: textColor,
      transition: 'all 0.3s ease',
      fontFamily: 'Arial, sans-serif',
      maxWidth: '400px'
    }}>
      <h2 style={{ margin: '0 0 16px 0' }}>ğŸ¨ Theme Toggle</h2>
      <p style={{ margin: '0 0 12px 0', fontSize: '14px' }}>
        Current Theme: <strong>{isDarkMode ? 'ğŸŒ™ Dark Mode' : 'â˜€ï¸ Light Mode'}</strong>
      </p>
      <button 
        onClick={handleThemeToggle}
        style={{
          padding: '12px 24px',
          fontSize: '16px',
          fontWeight: 'bold',
          border: borderStyle,
          borderRadius: '6px',
          backgroundColor: buttonBg,
          color: textColor,
          cursor: 'pointer',
          transition: 'background-color 0.2s ease',
          width: '100%',
          boxSizing: 'border-box'
        }}
        onMouseEnter={(e) => e.target.style.backgroundColor = buttonHoverBg}
        onMouseLeave={(e) => e.target.style.backgroundColor = buttonBg}
      >
        {isDarkMode ? 'â˜€ï¸ Switch to Light Mode' : 'ğŸŒ™ Switch to Dark Mode'}
      </button>
      <div style={{
        marginTop: '16px',
        padding: '12px',
        backgroundColor: isDarkMode ? '#2a2a2a' : '#f5f5f5',
        borderRadius: '4px',
        fontSize: '12px',
        opacity: 0.8
      }}>
        <p style={{ margin: '0' }}>âœ¨ Click the button to toggle between light and dark themes</p>
        <p style={{ margin: '4px 0 0 0' }}>ğŸ“¡ Theme changes are published to the conductor</p>
      </div>
    </div>
  );
}

